import { BackupGroupListJson } from './__json__/backup-group-list-json';
import { BackupGroup } from './backup-group';

/**
 * Backup Group List.
 */
/* istanbul ignore next: autogenerated */
export class BackupGroupList {

  constructor(private _json: BackupGroupListJson) {
  }

  /**
   * Get data.
   * @returns {Array<BackupGroup>}
   */
  get data(): Array<BackupGroup> {
    return this._json.data?.map(it => new BackupGroup(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {BackupGroupListJson}
   */
  get json(): BackupGroupListJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
