import { RemoteVdcBackupClusterInfo } from './remote-vdc-backup-cluster-info';
import { LocalVdcBackupClusterInfo } from './local-vdc-backup-cluster-info';
import { VdcBackupClusterInfoJson } from './__json__/vdc-backup-cluster-info-json';

/**
 * vDC Backup Cluster Info.
 */
 /* istanbul ignore next: autogenerated */
export class VdcBackupClusterInfo {

  constructor(private _json: VdcBackupClusterInfoJson) {
  }

  /**
   * Get vDC uuid.
   * @returns {string}
   */
  get vdcUuid(): string {
    return this._json.vdc_uuid;
  }

  /**
   * Get local cluster.
   * @returns {LocalVdcBackupClusterInfo}
   */
  get localCluster(): LocalVdcBackupClusterInfo {
    return new LocalVdcBackupClusterInfo(this._json.local_cluster);
  }

  /**
   * Get remote clusters.
   * @returns {Array<RemoteVdcBackupClusterInfo>}
   */
  get remoteClusters(): Array<RemoteVdcBackupClusterInfo> {
    return this._json.remote_clusters.map(it => new RemoteVdcBackupClusterInfo(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {VdcBackupClusterInfoJson}
   */
  get json(): VdcBackupClusterInfoJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
