import { BackupRunStatsJson } from './__json__/backup-run-stats-json';

/**
 * Backup Run Stats.
 */
/* istanbul ignore next: autogenerated */
export class BackupRunStats {

  constructor(private _json: BackupRunStatsJson) {
  }

  /**
   * Get admitted time.
   * @returns {number | null}
   */
  get admittedTime(): number | null {
    return this._json.admitted_time || null;
  }

  /**
   * Get end time.
   * @returns {number | null}
   */
  get endTime(): number | null {
    return this._json.end_time || null;
  }

  /**
   * Get num canceled tasks.
   * @returns {number}
   */
  get numCanceledTasks(): number {
    return this._json.num_canceled_tasks;
  }

  /**
   * Get num failed tasks.
   * @returns {number}
   */
  get numFailedTasks(): number {
    return this._json.num_failed_tasks;
  }

  /**
   * Get num successful tasks.
   * @returns {number}
   */
  get numSuccessfulTasks(): number {
    return this._json.num_successful_tasks;
  }

  /**
   * Get start time in milliseconds.
   * @returns {number}
   */
  get startTimeMillis(): number {
    return this._json.start_time_millis;
  }

  /**
   * Get start time usecs.
   * @deprecated
   * @returns {number}
   */
  get startTimeUsecs(): number {
    return this._json.start_time_usecs;
  }

  /**
   * Get time taken millis.
   * @returns {number | null}
   */
  get timeTakenMillis(): number | null {
    return this._json.time_taken_millis ?? null;
  }

  /**
   * Get total bytes read from source.
   * @returns {number}
   */
  get totalBytesReadFromSource(): number {
    return this._json.total_bytes_read_from_source;
  }

  /**
   * Get total bytes to read from source.
   * @returns {number}
   */
  get totalBytesToReadFromSource(): number {
    return this._json.total_bytes_to_read_from_source;
  }

  /**
   * Get total logical backup size bytes.
   * @returns {number}
   */
  get totalLogicalBackupSizeBytes(): number {
    return this._json.total_logical_backup_size_bytes;
  }

  /**
   * Get total physical backup size bytes.
   * @returns {number}
   */
  get totalPhysicalBackupSizeBytes(): number {
    return this._json.total_physical_backup_size_bytes;
  }

  /**
   * Get total source size bytes.
   * @returns {number}
   */
  get totalSourceSizeBytes(): number {
    return this._json.total_source_size_bytes;
  }

  /**
   * Get the json representation of this class.
   * @returns {BackupRunStatsJson}
   */
  get json(): BackupRunStatsJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
