import { BillJson } from './__json__/bill-json';
import { ResourceCostAndUsage } from './resource-cost-and-usage';
import { DiskCostAndUsage } from './disk-cost-and-usage';
import { EntityType } from '../__json__/entity-type';

/* istanbul ignore next: autogenerated */
export class Bill {

  constructor(private _json: BillJson) {
  }

  /**
   * Get bandwidth.
   * @returns {ResourceCostAndUsage}
   */
  get bandwidth(): ResourceCostAndUsage {
    return new ResourceCostAndUsage(this._json.bandwidth);
  }

  /**
   * Get cpu.
   * @returns {ResourceCostAndUsage}
   */
  get cpu(): ResourceCostAndUsage {
    return new ResourceCostAndUsage(this._json.cpu);
  }

  /**
   * Get memory.
   * @returns {ResourceCostAndUsageJson}
   */
  get memory(): ResourceCostAndUsage {
    return new ResourceCostAndUsage(this._json.memory);
  }

  /**
   * Get disk.
   * @returns {DiskCostAndUsage}
   */
  get disk(): DiskCostAndUsage {
    return new DiskCostAndUsage(this._json.disk);
  }

  /**
   * Get total cost.
   * @returns {number}
   */
  get totalCost(): number {
    return this._json.total_cost;
  }

  /**
   * Get total cost estimate.
   * @returns {number}
   */
  get totalCostEstimate(): number {
    return this._json.total_cost_estimate;
  }

  /**
   * Get year.
   * @returns {number}
   */
  get year(): number {
    return this._json.year;
  }

  /**
   * Get month.
   * @returns {number}
   */
  get month(): number {
    return this._json.month;
  }

  /**
   * Get entity uuid.
   * @returns {string}
   */
  get entityUuid(): string {
    return this._json.entity_uuid;
  }

  /**
   * Get entity name.
   * @returns {string}
   */
  get entityName(): string {
    return this._json.entity_name;
  }

  /**
   * Get entity type.
   * @returns {EntityType}
   */
  get entityType(): EntityType {
    return this._json.entity_type;
  }

  /**
   * Get the json representation of this class.
   * @returns {BillJson}
   */
  get json(): BillJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
