import { ResourceCostAndUsageJson } from './__json__/resource-cost-and-usage-json';
import { CostAndUsage } from './cost-and-usage';

/* istanbul ignore next: autogenerated */
export class ResourceCostAndUsage {

  constructor(private _json: ResourceCostAndUsageJson) {
  }

  /**
   * Get total.
   * @returns {CostAndUsage}
   */
  get total(): CostAndUsage {
    return new CostAndUsage(this._json.total);
  }

  /**
   * Get reserved.
   * @returns {CostAndUsage}
   */
  get reserved(): CostAndUsage {
    return new CostAndUsage(this._json.reserved);
  }

  /**
   * Get burst.
   * @returns {CostAndUsage}
   */
  get burst(): CostAndUsage {
    return new CostAndUsage(this._json.burst);
  }

  /**
   * Get the json representation of this class.
   * @returns {ResourceCostAndUsageJson}
   */
  get json(): ResourceCostAndUsageJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
