import { StorageProfileJson } from './__json__/storage-profile-json';

/**
 * Storage Profile.
 */
/* istanbul ignore next: autogenerated */
export class StorageProfile {

  constructor(private _json: StorageProfileJson) {
  }

  /**
   * Get uuid.
   * @returns {string}
   */
  get uuid(): string {
    return this._json.uuid;
  }

  /**
   * Get vdc uuid.
   * @returns {string}
   */
  get vdcUuid(): string {
    return this._json.vdc_uuid;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get default profile.
   * @returns {boolean}
   */
  get defaultProfile(): boolean {
    return this._json.default_profile;
  }

  /**
   * Get unit.
   * @returns {string}
   */
  get unit(): string {
    return this._json.unit;
  }

  /**
   * Get limit.
   * @returns {number}
   */
  get limit(): number {
    return this._json.limit;
  }

  /**
   * Get storage used in mb.
   * @returns {number}
   */
  get storageUsedInMb(): number {
    return this._json.storage_used_in_mb;
  }

  /**
   * Get the json representation of this class.
   * @returns {StorageProfileJson}
   */
  get json(): StorageProfileJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
