import { CloudTenantBillHistoryJson } from './__json__/cloud-tenant-billing-history-json';
import { CloudTenantBill } from './cloud-tenant-bill';

/**
 * Cloud Tenant Bill History.
 */
/* istanbul ignore next: autogenerated */
export class CloudTenantBillHistory {

  constructor(private _json: CloudTenantBillHistoryJson) {
  }

  /**
   * Get bills.
   * @returns {{ [key: string]: Array<CloudTenantBill> }}
   */
  get bills(): { [key: string]: Array<CloudTenantBill> } {
    const bills: { [key: string]: Array<CloudTenantBill> } = {};
    for (const k in this._json) {
      const v = this._json.bills[k];
      bills[k] = v.map(it => new CloudTenantBill(it));
    }
    return bills;
  }

  /**
   * Get the json representation of this class.
   * @returns {CloudTenantBillHistoryJson}
   */
  get json(): CloudTenantBillHistoryJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
