import { CloudTenantBillJson } from './__json__/cloud-tenant-bill-json';
import { CurrencyCode } from '../common/billing/__json__/currency-code-type';

/**
 * Cloud Tenant Bill.
 */
/* istanbul ignore next: autogenerated */
export class CloudTenantBill {

  constructor(private _json: CloudTenantBillJson) {
  }

  /**
   * Get total.
   * @returns {number}
   */
  get total(): number {
    return this._json.total;
  }

  /**
   * Get year.
   * @returns {number}
   */
  get year(): number {
    return this._json.year;
  }

  /**
   * Get month.
   * @returns {number}
   */
  get month(): number {
    return this._json.month;
  }

  /**
   * Get currency code.
   * @returns {CurrencyCode}
   */
  get currencyCode(): CurrencyCode {
    return this._json.currency_code;
  }

  /**
   * Get storage quota in gb.
   * @returns {number}
   */
  get storageQuotaInGb(): number {
    return this._json.storage_quota_in_gb;
  }

  /**
   * Get storage price per gb.
   * @returns {number}
   */
  get storagePricePerGb(): number {
    return this._json.storage_price_per_gb;
  }

  /**
   * Get full month contract cost.
   * @returns {number}
   */
  get fullMonthContractCost(): number {
    return this._json.full_month_contract_cost;
  }

  /**
   * Get the json representation of this class.
   * @returns {CloudTenantBillJson}
   */
  get json(): CloudTenantBillJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
