import { SupportServiceOptionJson } from './__json__/support-service-option-json';
import { SupportCategory } from './support-category';

/**
 * Support Service Option.
 */
/* istanbul ignore next: autogenerated */
export class SupportServiceOption {

  constructor(private _json: SupportServiceOptionJson) {
  }

  /**
   * Get id.
   * @returns {number}
   */
  get id(): number {
    return this._json.id;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get regarding option id.
   * @returns {number}
   */
  get regardingOptionId(): number {
    return this._json.regarding_option_id;
  }

  /**
   * Get support categories.
   * @returns {Array<SupportCategory>}
   */
  get supportCategories(): Array<SupportCategory> {
    return this._json.support_categories.map(it => new SupportCategory(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {SupportServiceOptionJson}
   */
  get json(): SupportServiceOptionJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
