import { VmProtectionSummaryJson } from './__json__/vm-protection-summary-json';

/**
 * VM Protection Status Summary Response
 */
/* istanbul ignore next: autogenerated */
export class VmProtectionSummary {

  constructor(private _json: VmProtectionSummaryJson) {
  }

  /**
   * Get the number of protected VMs.
   * @returns {number}
   */
  get vmsProtected(): number {
    return this._json.vms_protected;
  }

  /**
   * Get the number of unprotected VMs.
   * @returns {number}
   */
  get vmsUnprotected(): number {
    return this._json.vms_un_protected;
  }

  /**
   * Get the total number of VMs.
   * @returns {number}
   */
  get totalVms(): number {
    return this._json.total_vms;
  }

  /**
   * Get the json representation of this class.
   * @returns {VmProtectionSummaryJson}
   */
  get json(): VmProtectionSummaryJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
