import { EdgeGatewayRoutingJson } from './__json__/edge-gateway-routing-json';
import { RoutingGlobalConfig } from './routing-global-config';
import { RoutingStaticConfig } from './routing-static-config';
import { RoutingOSPFConfig } from './routing-ospf-config';
import { RoutingBGPConfig } from './routing-bgp-config';

/**
 * Edge Gateway Routing
 */

/* istanbul ignore next: autogenerated */
export class EdgeGatewayRouting {

  constructor(private _json: EdgeGatewayRoutingJson) {
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get routing global config.
   * @returns {RoutingGlobalConfig}
   */
  get routingGlobalConfig(): RoutingGlobalConfig {
    return new RoutingGlobalConfig(this._json.routing_global_config);
  }

  /**
   * Get routing static config.
   * @returns {RoutingStaticConfig}
   */
  get routingStaticConfig(): RoutingStaticConfig {
    return new RoutingStaticConfig(this._json.routing_static_config);
  }

  /**
   * Get routing ospf config.
   * @returns {RoutingOSPFConfig}
   */
  get routingOspfConfig(): RoutingOSPFConfig {
    return new RoutingOSPFConfig(this._json.routing_ospf_config);
  }

  /**
   * Get routing bgp config.
   * @returns {RoutingBGPConfig}
   */
  get routingBgpConfig(): RoutingBGPConfig {
    return new RoutingBGPConfig(this._json.routing_bgp_config);
  }

  /**
   * Get the json representation of this class.
   * @returns {EdgeGatewayRoutingJson}
   */
  get json(): EdgeGatewayRoutingJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
