import { LoggingJson } from './__json__/logging-json';
import { LogLevelType } from './__json__/log-level-type';

/**
 * Logging
 */
/* istanbul ignore next: autogenerated */
export class Logging {

  constructor(private _json: LoggingJson) {
  }

  /**
   * Get enable.
   * @returns {boolean}
   */
  get enable(): boolean {
    return this._json.enable;
  }

  /**
   * Get log level.
   * @returns {LogLevelType}
   */
  get logLevel(): LogLevelType {
    return this._json.log_level;
  }

  /**
   * Get the json representation of this class.
   * @returns {LoggingJson}
   */
  get json(): LoggingJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
