import { OSPFInterfaceJson } from './__json__/ospf-interface-json';

/**
 * OSPF Interface
 */
/* istanbul ignore next: autogenerated */
export class OSPFInterface {

  constructor(private _json: OSPFInterfaceJson) {
  }

  /**
   * Get vnic.
   * @returns {number}
   */
  get vnic(): number {
    return this._json.vnic;
  }

  /**
   * Get area id.
   * @returns {number}
   */
  get areaId(): number {
    return this._json.area_id;
  }

  /**
   * Get hello interval.
   * @returns {number}
   */
  get helloInterval(): number {
    return this._json.hello_interval;
  }

  /**
   * Get dead interval.
   * @returns {number}
   */
  get deadInterval(): number {
    return this._json.dead_interval;
  }

  /**
   * Get priority.
   * @returns {number}
   */
  get priority(): number {
    return this._json.priority;
  }

  /**
   * Get cost.
   * @returns {number}
   */
  get cost(): number {
    return this._json.cost;
  }

  /**
   * Get mtu ignore.
   * @returns {boolean}
   */
  get mtuIgnore(): boolean {
    return this._json.mtu_ignore;
  }

  /**
   * Get the json representation of this class.
   * @returns {OSPFInterfaceJson}
   */
  get json(): OSPFInterfaceJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
