import { RoutingBGPConfigUpdateRequestJson } from './__json__/routing-bgp-config-update-request-json';
import { BGPNeighbour } from './bgp-neighbour';
import { RedistributionUpdateRequest } from './redistribution-update-request';
import { RedistributionUpdateRequestJson } from './__json__/redistribution-update-request-json';
import { BGPNeighbourJson } from './__json__/bgp-neighbour-json';

/**
 * Routing BGP Configuration Update Request
 */
/* istanbul ignore next: autogenerated */
export class RoutingBGPConfigUpdateRequest {

  private readonly _json: RoutingBGPConfigUpdateRequestJson;

  constructor(routingBGPConfigUpdateRequest: RoutingBGPConfigUpdateRequest);
  constructor(routingBGPConfigUpdateRequestJson: RoutingBGPConfigUpdateRequestJson);
  constructor(enabled: boolean, localAsNumber: string, bgpNeighbours: Array<BGPNeighbourJson>,
              redistribution: RedistributionUpdateRequestJson, gracefulRestart: boolean, defaultOriginate: boolean);
  constructor(firstParam: boolean | RoutingBGPConfigUpdateRequest | RoutingBGPConfigUpdateRequestJson,
              localAsNumber?: string, bgpNeighbours?: Array<BGPNeighbourJson>,
              redistribution?: RedistributionUpdateRequestJson, gracefulRestart?: boolean, defaultOriginate?: boolean) {
    if (typeof firstParam === 'boolean') {
      // Parameters constructor
      this._json = {
        enabled: firstParam,
        local_as_number: localAsNumber,
        bgp_neighbours: bgpNeighbours,
        redistribution: redistribution,
        graceful_restart: gracefulRestart,
        default_originate: defaultOriginate
      } as RoutingBGPConfigUpdateRequestJson;
    } else if (firstParam instanceof RoutingBGPConfigUpdateRequest) {
      // Copy constructor
      this._json = (firstParam as RoutingBGPConfigUpdateRequest).json;
    } else {
      // Json or empty constructor
      this._json = (firstParam || {}) as RoutingBGPConfigUpdateRequestJson;
    }
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get local as number.
   * @returns {string}
   */
  get localAsNumber(): string {
    return this._json.local_as_number;
  }

  /**
   * Get bgp neighbours.
   * @returns {Array<BGPNeighbour>}
   */
  get bgpNeighbours(): Array<BGPNeighbour> {
    return this._json.bgp_neighbours.map(it => new BGPNeighbour(it));
  }

  /**
   * Get redistribution.
   * @returns {RedistributionUpdateRequest}
   */
  get redistribution(): RedistributionUpdateRequest {
    return new RedistributionUpdateRequest(this._json.redistribution);
  }

  /**
   * Get graceful restart.
   * @returns {boolean}
   */
  get gracefulRestart(): boolean {
    return this._json.graceful_restart;
  }

  /**
   * Get default originate.
   * @returns {boolean}
   */
  get defaultOriginate(): boolean {
    return this._json.default_originate;
  }

  /**
   * Get the json representation of this class.
   * @returns {RoutingBGPConfigUpdateRequestJson}
   */
  get json(): RoutingBGPConfigUpdateRequestJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
