import { RoutingGlobalConfigJson } from './__json__/routing-global-config-json';
import { Logging } from './logging';
import { IpPrefix } from './ip-prefix';

/**
 * Routing Global Configuration
 */
/* istanbul ignore next: autogenerated */
export class RoutingGlobalConfig {

  constructor(private _json: RoutingGlobalConfigJson) {
  }

  /**
   * Get router id.
   * @returns {string}
   */
  get routerId(): string {
    return this._json.router_id;
  }

  /**
   * Get ecmp.
   * @returns {boolean}
   */
  get ecmp(): boolean {
    return this._json.ecmp;
  }

  /**
   * Get logging.
   * @returns {Logging}
   */
  get logging(): Logging {
    return new Logging(this._json.logging);
  }

  /**
   * Get ip prefixes.
   * @returns {Array<IpPrefix>}
   */
  get ipPrefixes(): Array<IpPrefix> {
    return this._json.ip_prefixes.map(it => new IpPrefix(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {RoutingGlobalConfigJson}
   */
  get json(): RoutingGlobalConfigJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
