import { OSPFAreaJson } from './__json__/ospf-area-json';
import { RoutingOSPFConfigUpdateRequestJson } from './__json__/routing-ospf-config-update-request-json';
import { OSPFInterface } from './ospf-interface';
import { RedistributionUpdateRequestJson } from './__json__/redistribution-update-request-json';
import { RedistributionUpdateRequest } from './redistribution-update-request';
import { OSPFInterfaceJson } from './__json__/ospf-interface-json';

/**
 * Routing OSPF Configuration Update Request
 */
/* istanbul ignore next: autogenerated */
export class RoutingOSPFConfigUpdateRequest {

  private readonly _json: RoutingOSPFConfigUpdateRequestJson;

  constructor(routingOSPFConfigUpdateRequest: RoutingOSPFConfigUpdateRequest);
  constructor(routingOSPFConfigUpdateRequestJson: RoutingOSPFConfigUpdateRequestJson);
  constructor(enabled: boolean, gracefulRestart: boolean, defaultOriginate: boolean, ospfAreas: Array<OSPFAreaJson>,
              ospfInterfaces: Array<OSPFInterfaceJson>, redistribution: RedistributionUpdateRequestJson);
  constructor(firstParam: boolean | RoutingOSPFConfigUpdateRequest | RoutingOSPFConfigUpdateRequestJson,
              gracefulRestart?: boolean, defaultOriginate?: boolean, ospfAreas?: Array<OSPFAreaJson>,
              ospfInterfaces?: Array<OSPFInterfaceJson>, redistribution?: RedistributionUpdateRequestJson) {
    if (typeof firstParam === 'boolean') {
      // Parameters constructor
      this._json = {
        enabled: firstParam,
        graceful_restart: gracefulRestart,
        default_originate: defaultOriginate,
        ospf_areas: ospfAreas,
        ospf_interfaces: ospfInterfaces,
        redistribution: redistribution
      } as RoutingOSPFConfigUpdateRequestJson;
    } else if (firstParam instanceof RoutingOSPFConfigUpdateRequest) {
      // Copy constructor
      this._json = (firstParam as RoutingOSPFConfigUpdateRequest).json;
    } else {
      // Json or empty constructor
      this._json = (firstParam || {}) as RoutingOSPFConfigUpdateRequestJson;
    }
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get graceful restart.
   * @returns {boolean}
   */
  get gracefulRestart(): boolean {
    return this._json.graceful_restart;
  }

  /**
   * Get default originate.
   * @returns {boolean}
   */
  get defaultOriginate(): boolean {
    return this._json.default_originate;
  }

  /**
   * Get ospf areas.
   * @returns {Array<OSPFArea>}
   */
  get ospfAreas(): Array<OSPFAreaJson> {
    return this._json.ospf_areas;
  }

  /**
   * Get ospf interfaces.
   * @returns {Array<OSPFInterface>}
   */
  get ospfInterfaces(): Array<OSPFInterface> {
    return this._json.ospf_interfaces.map(it => new OSPFInterface(it));
  }

  /**
   * Get redistribution.
   * @returns {RedistributionUpdateRequest}
   */
  get redistribution(): RedistributionUpdateRequest {
    return new RedistributionUpdateRequest(this._json.redistribution);
  }

  /**
   * Get the json representation of this class.
   * @returns {RoutingOSPFConfigUpdateRequestJson}
   */
  get json(): RoutingOSPFConfigUpdateRequestJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
