import { Route } from './route';
import { RoutingStaticConfigJson } from './__json__/routing-static-config-json';
import { DefaultRoute } from './default-route';

/**
 * Routing Static Configuration
 */
/* istanbul ignore next: autogenerated */
export class RoutingStaticConfig {

  constructor(private _json: RoutingStaticConfigJson) {
  }

  /**
   * Get default route.
   * @returns {DefaultRoute | undefined}
   */
  get defaultRoute(): DefaultRoute | undefined {
    return this._json.default_route
      ? new DefaultRoute(this._json.default_route)
      : undefined;
  }

  /**
   * Get static routes.
   * @returns {Array<Route>}
   */
  get staticRoutes(): Array<Route> {
    return this._json.static_routes.map(it => new Route(it));
  }

  /**
   * Get the json representation of this class.
   * @returns {RoutingStaticConfigJson}
   */
  get json(): RoutingStaticConfigJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
