/* istanbul ignore next: autogenerated */
import { ActionType } from './__json__/action-type';
import { From } from './from';
import { RuleJson } from './__json__/rule-json';

/**
 * Rule
 */
export class Rule {

  constructor(private _json: RuleJson) {
  }

  /**
   * Get id.
   * @returns {number}
   */
  get id(): number {
    return this._json.id;
  }

  /**
   * Get prefix name.
   * @returns {string}
   */
  get prefixName(): string {
    return this._json.prefix_name;
  }

  /**
   * Get from.
   * @returns {From}
   */
  get from(): From {
    return new From(this._json.from);
  }

  /**
   * Get action.
   * @returns {ActionType}
   */
  get action(): ActionType {
    return this._json.action;
  }

  /**
   * Get the json representation of this class.
   * @returns {RuleJson}
   */
  get json(): RuleJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
