import { MacAddressJson } from './__json__/vnic-mac-address-json';

/**
 * Edge Gateway VNIC MAC Address.
 */
/* istanbul ignore next: autogenerated */
export class MacAddress {

  constructor(private _json: MacAddressJson) {
  }

  /**
   * Get edge vm ha index.
   * @returns {number}
   */
  get edgeVmHaIndex(): number {
    return this._json.edge_vm_ha_index;
  }

  /**
   * Get value.
   * @returns {string}
   */
  get value(): string {
    return this._json.value;
  }

  /**
   * Get the json representation of this class.
   * @returns {MacAddress}
   */
  get json(): MacAddressJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
