import { ShapingPolicyJson } from './__json__/vnic-shaping-policy-json';

/**
 * Edge Gateway VNIC Shaping Policy.
 */
/* istanbul ignore next: autogenerated */
export class ShapingPolicy {

  constructor(private _json: ShapingPolicyJson) {
  }

  /**
   * Get average bandwidth.
   * @returns {number}
   */
  get averageBandwidth(): number {
    return this._json.average_bandwidth;
  }

  /**
   * Get peak bandwidth.
   * @returns {number}
   */
  get peakBandwidth(): number {
    return this._json.peak_bandwidth;
  }

  /**
   * Get burst size.
   * @returns {number}
   */
  get burstSize(): number {
    return this._json.burst_size;
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get inherited.
   * @returns {boolean}
   */
  get inherited(): boolean {
    return this._json.inherited;
  }

  /**
   * Get the json representation of this class.
   * @returns {ShapingPolicy}
   */
  get json(): ShapingPolicyJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
