import { FirewallRestorePointJson } from './__json__/firewall-restore-point-json';

/**
 * Firewall Restore Point.
 */
/* istanbul ignore next: autogenerated */
export class FirewallRestorePoint {

  constructor(private _json: FirewallRestorePointJson) {
  }

  /**
   * Get description.
   * @returns {string}
   */
  get description(): string {
    return this._json.description;
  }

  /**
   * Get restore point time.
   * @returns {number}
   */
  get restorePointTime(): Date {
    return new Date(this._json.restore_point_time);
  }

  /**
   * Get the json representation of this class.
   * @returns {FirewallRestorePointJson}
   */
  get json(): FirewallRestorePointJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
