import { RestoreFirewallRequestJson } from './__json__/restore-firewall-request-json';

/**
 * Restore Firewall Request.
 */
/* istanbul ignore next: autogenerated */
export class RestoreFirewallRequest {

  private readonly _json: RestoreFirewallRequestJson;

  constructor(restoreFirewallRequest: RestoreFirewallRequest);
  constructor(restoreFirewallRequestJson: RestoreFirewallRequestJson);
  constructor(restorePointTime: number);
  constructor(firstParam: number | Date | RestoreFirewallRequest | RestoreFirewallRequestJson) {
    if (typeof firstParam === 'number') {
      // Parameters constructor
      this._json = {
        restore_point_time: firstParam
      } as RestoreFirewallRequestJson;
    } else if (firstParam instanceof Date) {
      this._json = {
        restore_point_time: firstParam.getTime()
      } as RestoreFirewallRequestJson;
    } else if (firstParam instanceof RestoreFirewallRequest) {
      // Copy constructor
      this._json = (firstParam as RestoreFirewallRequest).json;
    } else {
      // Json or empty constructor
      this._json = (firstParam || {}) as RestoreFirewallRequestJson;
    }
  }

  /**
   * Get restore point time.
   * @returns {number}
   */
  get restorePointTime(): Date {
    return new Date(this._json.restore_point_time);
  }

  /**
   * Get the json representation of this class.
   * @returns {RestoreFirewallRequestJson}
   */
  get json(): RestoreFirewallRequestJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
