import { EdgeStaticRoutingServiceJson } from './__json__/edge-static-routing-service-json';
import { EdgeStaticRouteJson } from './__json__/edge-static-route-json';

/**
 * Edge Static Routing Service
 */
/* istanbul ignore next: autogenerated */
export class EdgeStaticRoutingService {

  constructor(private _json: EdgeStaticRoutingServiceJson) {
  }

  /**
   * Get edge uuid.
   * @returns {string}
   */
  get edgeUuid(): string {
    return this._json.edge_uuid;
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get routes.
   * @returns {Array<EdgeStaticRouteJson>}
   */
  get routes(): Array<EdgeStaticRouteJson> {
    return this._json.routes;
  }

  /**
   * Get the json representation of this class.
   * @returns {EdgeStaticRoutingServiceJson}
   */
  get json(): EdgeStaticRoutingServiceJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
