import { RecoveryGroupReportDetailsJson } from './__json__/recovery-group-report-detail-json';
import { VpgFailoverReportDetails } from '../../vpg/vpg-failover/vpg-failover-report-details';
import { VmBootupLog } from './vm-bootlog';

/* istanbul ignore next: autogenerated */
export class RecoveryGroupReportDetails {

  constructor(private _json: RecoveryGroupReportDetailsJson) {
  }

  /**
   * Get recovery group uuid.
   * @returns {string}
   */
  get recoveryGroupUuid(): string {
    return this._json.recovery_group_uuid;
  }

  /**
   * Get failover status.
   * @returns {string}
   */
  get failoverStatus(): string {
    return this._json.failover_status;
  }

  /**
   * Get details.
   * @returns {VpgFailoverReportDetails}
   */
  get details(): VpgFailoverReportDetails {
    return new VpgFailoverReportDetails(this._json.details);
  }

  /**
   * Get bootup logs.
   * @returns {Array<VmBootupLog>}
   */
  get bootupLogs(): Array<VmBootupLog> {
    return this._json.bootup_logs.map(log => {
      return new VmBootupLog(log);
    });
  }

  /**
   * Get the json representation of this class.
   * @returns {RecoveryGroupReportDetailsJson}
   */
  get json(): RecoveryGroupReportDetailsJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
