import { JpegImage } from '../../common/jpeg-image';
import { VmBootupLogJson } from './__json__/vm-bootlog-json';

/**
 * Vm bootup log
 */
/* istanbul ignore next: autogenerated */
export class VmBootupLog {

  constructor(private _json: VmBootupLogJson) {
  }

  /**
   * Get vm uuid.
   * @returns {string}
   */
  get vmUuid(): string {
    return this._json.vm_uuid;
  }

  /**
   * Get vm name.
   * @returns {string}
   */
  get vmName(): string {
    return this._json.vm_name;
  }

  /**
   * Get screenshot.
   * @returns {JpegImage}
   */
  get screenshot(): JpegImage {
    const screenShotBuff: Buffer = new Buffer(this._json.screenshot);
    return new JpegImage(screenShotBuff);
  }

  /**
   * Get timestamp.
   * @returns {number}
   */
  get timestamp(): number {
    return this._json.timestamp;
  }

  /**
   * Get ip address.
   * @returns {string}
   */
  get ipAddress(): string {
    return this._json.ip_address;
  }

  /**
   * Get error log.
   * @returns {string}
   */
  get errorLog(): string {
    return this._json.error_log;
  }

  /**
   * Get the json representation of this class.
   * @returns {VmBootupLogJson}
   */
  get json(): VmBootupLogJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
