import { ExternalNetworkJson } from './__json__/external-network-json';
import { NetworkIpScope } from '../common/network-ip-scope';

/* istanbul ignore next: autogenerated */
export class ExternalNetwork {

  constructor(private _json: ExternalNetworkJson) {
  }

  /**
   * Get graph id1.
   * @returns {string}
   */
  get graphId1(): string {
    return this._json.graph_id1;
  }

  /**
   * Get graph id2.
   * @returns {string}
   */
  get graphId2(): string {
    return this._json.graph_id2;
  }

  /**
   * Get ip scope.
   * @returns {NetworkIpScope}
   */
  get ipScope(): NetworkIpScope {
    return new NetworkIpScope(this._json.ip_scope);
  }

  /**
   * Get ip scopes.
   * @returns {Array<NetworkIpScope>}
   */
  get ipScopes(): Array<NetworkIpScope> {
    return this._json.ip_scopes.map((scope) => { return new NetworkIpScope(scope); });
  }

  /**
   * Get uuid.
   * @returns {string}
   */
  get uuid(): string {
    return this._json.uuid;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get deleted.
   * @returns {boolean}
   */
  get deleted(): boolean {
    return this._json.deleted;
  }

  /**
   * Get deleted date.
   * @returns {number}
   */
  get deletedDate(): number {
    return this._json.deleted_date;
  }

  /**
   * Get updated date.
   * @returns {number}
   */
  get updatedDate(): number {
    return this._json.updated_date;
  }

  /**
   * Get the json representation of this class.
   * @returns {ExternalNetworkJson}
   */
  get json(): ExternalNetworkJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
