import { NessusHostJson } from './__json__/nessus-host-json';

/**
 * Nessus Host.
 */

/* istanbul ignore next: autogenerated */
export class NessusHost {

  constructor(private _json: NessusHostJson) {
  }

  /**
   * Get host id.
   * @returns {number}
   */
  get hostId(): number {
    return this._json.host_id;
  }

  /**
   * Get host index.
   * @returns {string}
   */
  get hostIndex(): string {
    return this._json.host_index;
  }

  /**
   * Get host name.
   * @returns {number}
   */
  get hostName(): number {
    return this._json.host_name;
  }

  /**
   * Get progress.
   * @returns {string}
   */
  get progress(): string {
    return this._json.progress;
  }

  /**
   * Get critical.
   * @returns {number}
   */
  get critical(): number {
    return this._json.critical;
  }

  /**
   * Get high.
   * @returns {number}
   */
  get high(): number {
    return this._json.high;
  }

  /**
   * Get medium.
   * @returns {number}
   */
  get medium(): number {
    return this._json.medium;
  }

  /**
   * Get low.
   * @returns {number}
   */
  get low(): number {
    return this._json.low;
  }

  /**
   * Get info.
   * @returns {number}
   */
  get info(): number {
    return this._json.info;
  }

  /**
   * Get total checks considered.
   * @returns {number}
   */
  get totalChecksConsidered(): number {
    return this._json.total_checks_considered;
  }

  /**
   * Get num checks considered.
   * @returns {number}
   */
  get numChecksConsidered(): number {
    return this._json.num_checks_considered;
  }

  /**
   * Get scan progress total.
   * @returns {number}
   */
  get scanProgressTotal(): number {
    return this._json.scan_progress_total;
  }

  /**
   * Get scan progress current.
   * @returns {number}
   */
  get scanProgressCurrent(): number {
    return this._json.scan_progress_current;
  }

  /**
   * Get score.
   * @returns {number}
   */
  get score(): number {
    return this._json.score;
  }

  /**
   * Get the json representation of this class.
   * @returns {NessusHostJson}
   */
  get json(): NessusHostJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
