import { NessusNoteJson } from './__json__/nessus-note-json';

/**
 * Nessus Note.
 */

/* istanbul ignore next: autogenerated */
export class NessusNote {

  constructor(private _json: NessusNoteJson) {
  }

  /**
   * Get title.
   * @returns {string}
   */
  get title(): string {
    return this._json.title;
  }

  /**
   * Get message.
   * @returns {string}
   */
  get message(): string {
    return this._json.message;
  }

  /**
   * Get severity.
   * @returns {number}
   */
  get severity(): number {
    return this._json.severity;
  }

  /**
   * Get the json representation of this class.
   * @returns {NessusNoteJson}
   */
  get json(): NessusNoteJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
