import { NessusRemediationJson } from './__json__/nessus-remediation-json';

/**
 * Nessus Remediation.
 */
/* istanbul ignore next: autogenerated */
export class NessusRemediation {

  constructor(private _json: NessusRemediationJson) {
  }

  /**
   * Get value.
   * @returns {string}
   */
  get value(): string {
    return this._json.value;
  }

  /**
   * Get remediation.
   * @returns {string}
   */
  get remediation(): string {
    return this._json.remediation;
  }

  /**
   * Get hosts.
   * @returns {number}
   */
  get hosts(): number {
    return this._json.hosts;
  }

  /**
   * Get vulns.
   * @returns {number}
   */
  get vulns(): number {
    return this._json.vulns;
  }

  /**
   * Get the json representation of this class.
   * @returns {NessusRemediationJson}
   */
  get json(): NessusRemediationJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
