import { OrgCurrencyCodeJson } from './__json__/org-currency-code-json';
import { CurrencyCode } from '../common/billing/__json__';

/**
 * Org Currency Code.
 */
/* istanbul ignore next: autogenerated */
export class OrgCurrencyCode {

  constructor(private _json: OrgCurrencyCodeJson) {
  }

  /**
   * Get currency code.
   * @returns {CurrencyCode}
   */
  get currencyCode(): CurrencyCode {
    return this._json.currency_code;
  }

  /**
   * Get the json representation of this class.
   * @returns {OrgCurrencyCodeJson}
   */
  get json(): OrgCurrencyCodeJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
