import { VpgsProtectionInfoJson } from './__json__/vpg-protection-info-json';

/**
 * Org VPG Protection Info.
 */
/* istanbul ignore next: autogenerated */
export class VpgsProtectionInfo {

  constructor(private _json: VpgsProtectionInfoJson) {
  }

  /**
   * Get total replicated vms.
   * @returns {number}
   */
  get totalReplicatedVms(): number {
    return this._json.total_replicated_vms;
  }

  /**
   * Get total unreplicated vms.
   * @returns {number}
   */
  get totalUnreplicatedVms(): number {
    return this._json.total_unreplicated_vms;
  }

  /**
   * Get the json representation of this class.
   * @returns {VpgsProtectionInfoJson}
   */
  get json(): VpgsProtectionInfoJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
