import { VappNetworkStaticRoutingServiceJson } from './__json__';
import { VappNetworkStaticRoute } from './vapp-network-static-route';

/* istanbul ignore next: autogenerated */
export class VappNetworkStaticRoutingService {

  constructor(private _json: VappNetworkStaticRoutingServiceJson) {
  }

  /**
   * Get vapp uuid.
   * @returns {string}
   */
  get vappUuid(): string {
    return this._json.vapp_uuid;
  }

  /**
   * Get network name.
   * @returns {string}
   */
  get networkName(): string {
    return this._json.network_name;
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get static routes.
   * @returns {Array<VappNetworkStaticRoute>}
   */
  get staticRoutes(): Array<VappNetworkStaticRoute> {
    return this._json.static_routes.map((route) => {
      return new VappNetworkStaticRoute(route);
    });
  }

  /**
   * Get the json representation of this class.
   * @returns {VappNetworkStaticRoutingServiceJson}
   */
  get json(): VappNetworkStaticRoutingServiceJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
