import { TemplateDiskConfigurationJson } from './__json__/template-disk-configuration-json';
import { DiskType } from '../common/disk-type';

/**
 * Template Disk Configuration.
 */
/* istanbul ignore next: autogenerated */
export class TemplateDiskConfiguration {

  constructor(private _json: TemplateDiskConfigurationJson) {
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get size in bytes.
   * @returns {number}
   */
  get sizeInBytes(): number {
    return this._json.size_in_bytes;
  }

  /**
   * Get disk type.
   * @returns {DiskType}
   */
  get diskType(): DiskType {
    return this._json.disk_type;
  }

  /**
   * Get the json representation of this class.
   * @returns {TemplateDiskConfigurationJson}
   */
  get json(): TemplateDiskConfigurationJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
