import { VmTemplateConfiguration } from './vm-template-configuration';
import { VappTemplateNetworkConfiguration } from './vapp-template-network-configuration';
import { VappTemplateConfigurationJson } from './__json__/vapp-template-configuration-json';

/**
 * Vapp Template Configuration Response.
 */
/* istanbul ignore next: autogenerated */
export class VappTemplateConfiguration {

  constructor(private _json: VappTemplateConfigurationJson) {
  }

  /**
   * Get uuid.
   * @returns {string}
   */
  get uuid(): string {
    return this._json.uuid;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get description.
   * @returns {string}
   */
  get description(): string {
    return this._json.description;
  }

  /**
   * Get vms.
   * @returns {Array<VmTemplateConfigurationJson>}
   */
  get vms(): Array<VmTemplateConfiguration> {
    return this._json.vms.map((vmTplConfig) => new VmTemplateConfiguration(vmTplConfig));
  }

  /**
   * Get networks.
   * @returns {Array<VappTemplateNetworkConfiguration>}
   */
  get networks(): Array<VappTemplateNetworkConfiguration> {
    return this._json.networks.map((tplConfig) => new VappTemplateNetworkConfiguration(tplConfig));
  }

  /**
   * Get the json representation of this class.
   * @returns {VappTemplateConfigurationJson}
   */
  get json(): VappTemplateConfigurationJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
