import { VappTemplateDownloadDetailsJson } from './__json__/vapp-template-download-details-json';

/**
 * vApp template Download Details.
 */
/* istanbul ignore next: autogenerated */
export class VappTemplateDownloadDetails {

  constructor(private _json: VappTemplateDownloadDetailsJson) {
  }

  /**
   * Get enabled.
   * @returns {boolean}
   */
  get enabled(): boolean {
    return this._json.enabled;
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get size in bytes.
   * @returns {number}
   */
  get sizeInBytes(): number {
    return this._json.size_in_bytes;
  }

  /**
   * Get the json representation of this class.
   * @returns {VappTemplateDownloadDetailsJson}
   */
  get json(): VappTemplateDownloadDetailsJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
