import { VappTemplateNetworkConfigurationJson } from './__json__/vapp-template-network-configuration-json';
import { NetworkFenceMode } from '../common/network-fence-mode-type';
import { NetworkIpScope } from '../common/network-ip-scope';

/**
 * vApp Template Network Configuration Response.
 */
/* istanbul ignore next: autogenerated */
export class VappTemplateNetworkConfiguration {

  constructor(private _json: VappTemplateNetworkConfigurationJson) {
  }

  /**
   * Get name.
   * @returns {string}
   */
  get name(): string {
    return this._json.name;
  }

  /**
   * Get description.
   * @returns {string}
   */
  get description(): string {
    return this._json.description;
  }

  /**
   * Get fence mode.
   * @returns {NetworkFenceMode}
   */
  get fenceMode(): NetworkFenceMode {
    return this._json.fence_mode;
  }

  /**
   * Get ip scope.
   * @returns {NetworkIpScope}
   */
  get ipScope(): NetworkIpScope {
    return new NetworkIpScope(this._json.ip_scope);
  }

  /**
   * Get parent network name.
   * @returns {string}
   */
  get parentNetworkName(): string {
    return this._json.parent_network_name;
  }

  /**
   * Get the json representation of this class.
   * @returns {VappTemplateNetworkConfigurationJson}
   */
  get json(): VappTemplateNetworkConfigurationJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
