import { VmSummaryMapResponseJson } from './__json__/vm-ressource-summary-map-response-json';
import { VmSummary } from '../../vm/summary/vm-summary';

/**
 * Vm resource summary map response.
 */
/* istanbul ignore next: autogenerated */
export class VmResourceSummaryMapResponse {

  constructor(private _json: VmSummaryMapResponseJson) {
  }

  /**
   * Get summaries.
   * @returns {{ [key: string]: VmSummary }}
   */
  get summaries(): { [key: string]: VmSummary } {
    const map: { [key: string]: VmSummary } = {};
    Object.keys(this._json.summaries).forEach(key => {
      map[key] = new VmSummary(this._json.summaries[key]);
    });
    return map;
  }

  /**
   * Get the json representation of this class.
   * @returns {VmSummaryMapResponseJson}
   */
  get json(): VmSummaryMapResponseJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
