import { VmResourceSummaryJson } from './__json__/vm-resource-summary-json';

/**
 * VM Resource Summary.
 */
/* istanbul ignore next: autogenerated */
export class VmResourceSummary {

  constructor(private _json: VmResourceSummaryJson) {
  }

  /**
   * Get reserved cpu.
   * @returns {number}
   */
  get reservedCpu(): number {
    return this._json.reserved_cpu;
  }

  /**
   * Get reserved mem.
   * @returns {number}
   */
  get reservedMem(): number {
    return this._json.reserved_mem;
  }

  /**
   * Get consumed cpu.
   * @returns {number}
   */
  get consumedCpu(): number {
    return this._json.consumed_cpu;
  }

  /**
   * Get consumed mem.
   * @returns {number}
   */
  get consumedMem(): number {
    return this._json.consumed_mem;
  }

  /**
   * Get consumed disk.
   * @returns {number}
   */
  get consumedDisk(): number {
    return this._json.consumed_disk;
  }

  /**
   * Get provisioned disk.
   * @returns {number}
   */
  get provisionedDisk(): number {
    return this._json.provisioned_disk;
  }

  /**
   * Get configured disk.
   * @returns {number}
   */
  get configuredDisk(): number {
    return this._json.configured_disk;
  }

  /**
   * Get the json representation of this class.
   * @returns {VmResourceSummaryJson}
   */
  get json(): VmResourceSummaryJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
